function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
       var gr = new GlideRecord("x_336554_cussurvey_submitted_survey");
    //gr.addEncodedQuery('year=2020^month=2^e_mail_address=stefan.reichelt@comparex.com^survey_link=9e54c1fa2f3b0850c18d5e972799b615');
    //gr.addEncodedQuery('year=' + g_form.getValue('year') + '^month=' + g_form.getValue('month') + '^e_mail_address=' + g_form.getValue('e_mail_address') + '^survey_link=' + g_form.getValue('survey_link'));
	//gr.addQuery('year',g_form.getValue('year'));
	//gr.addQuery('month',g_form.getValue('month'));
	gr.addQuery('e_mail_address',g_form.getValue('e_mail_address'));
	gr.addQuery('survey_link',g_form.getValue('survey_link'));
    gr.query(surveyCallback);

	function surveyCallback(gr){

    var existingSurveys = gr.getRowCount();
    if (existingSurveys > 0) {
		g_form.addErrorMessage('There is already a submission for the time period selected.');
		g_form.setValue('e_mail_address','');
    }
	}
}
