catalog_script_client.script:
function onLoad() {
    //Take over param values; if not defined, set defaults
    var currentdate = new Date();

    var currentmonth = currentdate.getMonth() + 1;
    var currentYear = currentdate.getFullYear();

    var paramYear = getParameterValue('year');
    var paramMonth = getParameterValue('month');
    var paramSurveySysId = getParameterValue('survey_link');

    if (paramMonth.length > 0) {
        g_form.setValue('month', paramMonth);
    } else {
        g_form.setValue('month', currentmonth);
    }

    if (paramYear.length > 0) {
        g_form.setValue('year', paramYear);
    } else {
        g_form.setValue('year', currentYear);
    }

    var verifiedSurveyLinkId = '';
    querySurveyLink(paramSurveySysId);

    function querySurveyLink(survey_link_id) {
        var returnedSysId = '';
        var survey_link_object = new GlideRecord("x_336554_cussurvey_survey_link");
        survey_link_object.addQuery('sys_id', survey_link_id);
        survey_link_object.setLimit(1);
        survey_link_object.query(myCallbackFunction);

        function myCallbackFunction(survey_link_object) {
            while (survey_link_object.next()) {
                g_form.setValue('survey_link', survey_link_object.sys_id);
                g_form.setValue('company', survey_link_object.company);
                verifiedSurveyLinkId = survey_link_object.sys_id;
            }
        }
    }

    //Snippet source: https://www.servicenowguru.com/scripting/client-scripts-scripting/parse-url-parameters-client-script/
    function getParameterValue(name) {
        name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
        var regexS = "[\\?&]" + name + "=([^&#]*)";
        var regex = new RegExp(regexS);
        var results = regex.exec(top.location);
        if (results == null) {
            return "";
        } else {
            return unescape(results[1]);
        }
    }
}
